# DROP DATABASE anime;
CREATE DATABASE anime;
use anime;

CREATE TABLE animes (
  `id` int NOT NULL AUTO_INCREMENT,
  `name` varchar(100) NOT NULL,
  `description` varchar(1000) NOT NULL,
  `type` enum('TV','Film','OVA','Special') NOT NULL,
  `year` int NOT NULL,
  `image` varchar(1000) NOT NULL,
  `originalname` varchar(200) NOT NULL,
  `rating` varchar(50) NOT NULL,
  `demography` varchar(200) NOT NULL,
  `genre` varchar(200) NOT NULL,
  `image2` varchar(1000) NOT NULL,
  `image3` varchar(1000) NOT NULL,
  `active` varchar(200) NOT NULL,
  PRIMARY KEY (`id`)
);

CREATE TABLE users (
  `id` int primary key AUTO_INCREMENT,
  `name` varchar(50) NOT NULL,
  `email` varchar(30) NOT NULL,
  `password` varchar(30) NOT NULL,
  `phone` varchar(30) NOT NULL
);

CREATE TABLE favorite (
  `iduser` int NOT NULL,
  `idanime` int NOT NULL,
  PRIMARY KEY (`iduser`,`idanime`),
   CONSTRAINT `fk_user` FOREIGN KEY (`iduser`) REFERENCES `users` (`id`),
   CONSTRAINT `fk_animes2` FOREIGN KEY (`idanime`) REFERENCES `animes` (`id`)
);

CREATE TABLE videos (
  `idanime` int NOT NULL,
  `episode` int NOT NULL,
  `url` varchar(200) NOT NULL,
  `image` varchar(200) DEFAULT NULL,
   CONSTRAINT `fk_animes` FOREIGN KEY (`idanime`) REFERENCES `animes` (`id`)
);







